import React, {Component} from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Link} from "react-router-dom";
import "../App.css";
// import Button from 'react-bootstrap/Button'

// import "bootstrap/dist/css/bootstrap.min.css";
import Button from 'react-bootstrap/Button';

export default class Search extends Component {

    constructor(props) {
       super(props);
        this.state = {
            listings: [],
            search: '',
            x: 0,
            y: 0,
            type: 'Full-Time',
            duration: 0,
            applied_jobs:[]

        }
        this.onChangeSearch = this.onChangeSearch.bind(this);
        this.onChangeX = this.onChangeX.bind(this);
        this.onChangeY = this.onChangeY.bind(this);
        this.onChangeType = this.onChangeType.bind(this);
        this.onChangeDuration = this.onChangeDuration.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.FilterSalary = this.FilterSalary.bind(this);
        this.FilterType = this.FilterType.bind(this);
        this.FilterDuration = this.FilterDuration.bind(this);
        // this.getall = this.getall.bind(this);

    }

    onChangeSearch(event) {
        this.setState({ search: event.target.value });
    }
    onChangeX(event) {
        this.setState({ x: event.target.value });
    }
    onChangeY(event) {
        this.setState({ y: event.target.value });
    }
    onChangeType(event) {
        this.setState({ type: event.target.value });
    }
    onChangeDuration(event) {
        this.setState({ duration: event.target.value });
    }
    onSubmit(e) {

        e.preventDefault();
        let listings = this.state.listings;
        console.log(listings);
        const Search = {
            name: this.state.search,
        }

        console.log(Search);
        axios.post('http://localhost:4000/api/jobs/search', Search)
             .then(res => {
                console.log(res.data);
                this.setState({listings: res.data});

            })
             .catch(err =>
                {
                    if(err.response.data.message)
                    alert(err.response.data.message);
                    console.log(err)
                });

        this.setState({
            search : '',
        });
    }


    sortbySalary = () =>{
        let listings = this.state.listings, n = listings.length;

        for(var i=0; i < n-1; i++)
        {
            for(var j=0; j < n-1; j++)
            {
                var left = listings[j].salary;
                var left2 = listings[j+1].salary;
                if(left > left2)
                {
                    var temp = listings[j];
                    listings[j] = listings[j+1];
                    listings[j+1] = temp;
                }
            }
        }
        this.setState({listings: listings});
    }
    sortbydecSalary = () =>{
        let listings = this.state.listings, n = listings.length;

        for(var i=0; i < n-1; i++)
        {
            for(var j=0; j < n-1; j++)
            {
                var left = listings[j].salary;
                var left2 = listings[j+1].salary;
                if(left < left2)
                {
                    var temp = listings[j];
                    listings[j] = listings[j+1];
                    listings[j+1] = temp;
                }
            }
        }
        this.setState({listings: listings});
    }
    sortbyRating = () =>{
